mapper.select:
select count(*)
from tbl_week
where fk_code=#{code}

select classboardseq, fk_hakbun, fk_code, fk_lessonseq, name, subject, content, to_char(to_date(regdate), 'yyyy-mm-dd') AS regdate, to_char(to_date(regdate)+7, 'yyyy-mm-dd') AS enddate, status, filename, orgfilename, filesize
from tbl_classContentBoard
where fk_lessonseq = #{lessonseq}

select classboardseq, fk_hakbun, fk_code, fk_lessonseq, name, subject, content, to_char(to_date(regdate), 'yyyy-mm-dd') AS regdate, to_char(to_date(regdate)+7, 'yyyy-mm-dd') AS enddate, status, filename, orgfilename, filesize
from tbl_classContentBoard
where classboardseq = #{classBoardSeq}

mapper.update:
		update tbl_classContentBoard set subject = #{subject}
                           				, content = #{content}
        where classBoardSeq = #{classBoardSeq}

		update tbl_classContentBoard set subject = #{subject}
                           				, content = #{content}
                           				, fileName = #{fileName}
                           				, orgFilename = #{orgFilename}
                           				, fileSize = #{fileSize}
        where classBoardSeq = #{classBoardSeq}

mapper.select:
select classCommentSeq, name, fk_hakbun, commentContent, REGDATE,fk_classBoardSeq, FILENAME, ORGFILENAME, fileSize from ( select row_number() over(order by classCommentSeq desc) as rno, classCommentSeq, name, commentContent, fk_hakbun, to_char(to_date(regDate), 'yyyy-mm-dd') AS REGDATE,fk_classBoardSeq, nvl(FILENAME, ' ') as FILENAME, nvl(ORGFILENAME, ' ') as ORGFILENAME, nvl(to_char(FILESIZE), ' ') as fileSize from tbl_classContentComment where status = 1 and fk_classBoardSeq = #{fk_classBoardSeq}
and name like '%'||lower( #{searchWord} )||'%'
) V where rno between #{startRno} and #{endRno}

select count(*)
from tbl_attend
where fk_hakbun = #{fk_hakbun} and fk_lessonseq = #{fk_lessonseq}

select count(*)
from tbl_attend
where fk_hakbun = #{fk_hakbun} and fk_lessonseq = #{fk_lessonseq}
