mapper.select:
select previousseq, previoussubject, seq, fk_hakbun, name, subject, content, readCount, regDate, nextseq, nextsubject, fk_code from ( select lag(seq, 1) over(order by seq desc) AS previousseq , lag(subject, 1) over(order by seq desc) AS previoussubject , seq, fk_hakbun, name, subject, content, readCount , to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') AS regDate , lead(seq, 1) over(order by seq desc) AS nextseq , lead(subject, 1) over(order by seq desc) AS nextsubject , fk_code from tbl_FreeBoard where status = 1 and fk_code = #{fk_code}
and lower(${searchType}) like '%'||lower(#{searchWord}) ||'%'
) V where V.seq = #{seq}

mapper.update:
update tbl_FreeBoard set subject = #{subject}
                       , content = #{content}
where seq = #{seq} and pw = #{pw}

mapper.select:
 
	    
	        
	    	    select distinct ${searchType}    
	        
	        
	            select ${searchType} 
	        
	    
	    from tbl_FreeBoard
	    where status = 1 
	    and lower( ${searchType} ) like '%'|| lower( #{searchWord} ) ||'%'
	    
	    	order by seq desc

select seq, fk_hakbun, name, subject, readCount, regDate, commentCount, fk_code from ( select row_number() over(order by seq desc) AS rno , seq, fk_hakbun, name, subject , readCount, to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') as regDate , commentCount , fk_code from tbl_FreeBoard where status = 1 and fk_code = #{fk_code}
and lower( ${searchType} ) like '%'|| lower( #{searchWord} ) ||'%'
) V where rno between #{startRno} and #{endRno}

    select name, content, regDate
	from 
	(
	    select row_number() over(order by seq desc) AS rno
	         , name, content
	         , to_char(regDate, 'yyyy-mm-dd hh24:mi:ss') AS regDate 
	    from tbl_FreeComment
	    where status = 1 and parentSeq = #{parentSeq}
	) V 
	where rno between #{startRno} and #{endRno}

select count(*)
from tbl_FreeComment
where status = 1 and parentSeq = #{parentSeq}

    	select jseq, receive_hakbun, sender_hakbun, jjokjiTime, jjokjiContent, fileName, orgFilename, fileSize
		from tbl_jjokji
		where jseq = #{jseq}
